function resizeToMax(e){myImage=new Image;var t=e;myImage.src=t.src,myImage.width/document.body.clientWidth>myImage.height/document.body.clientHeight?t.style.width="100%":t.style.height="100%"}$(document).ready(function(){$.each($(".carousel-image"),function(e,t){resizeToMax(t)}),$(".carousel").carousel({interval:1e3,wrap:!0,pause:null});var e=$(".item").size();setInterval(function(){window.location.reload()},1e3*e)});